const xmlRaw = $json.data;

// Replace all single quotes in tags with double quotes
const cleanedXml = xmlRaw.replace(/<[^>]+>/g, tag =>
  tag.replace(/'/g, '"')
);

return [{ json: { xml: cleanedXml } }];

const header = [
  "Publish Date and Time",
  "Crossing Location",
  "Crossing Type",
  "General Lane Delay",
  "General Lane Number of Lanes",
  "Fast Lane Delay",
  "Fast Lane Number of Lanes"
].map(h => `"${h}"`).join(',');


const rows = $('extract_fields_multiple').all().map(item => {
  const {
    pubDate,
    location,
    cross_type,
    genLaneDelay,
    genLaneNum,
    fastLaneDelay,
    fastLaneNum
  } = item.json;

  // const escape = str => `"${String(str).replace(/"/g, '""').trim()}"`;

  const escape = str => String(str).replace(/"/g, '""').trim();

  return [
    escape(pubDate),
    escape(location),
    escape(cross_type),
    escape(genLaneDelay),
    escape(genLaneNum),
    escape(fastLaneDelay),
    escape(fastLaneNum)
  ].join(',');
});

// return [{ json: { csvRow: [header, ...rows].join('\n') } }];
