const csvContent = [header, ...rows].join('\n')+ '\n';
const buffer = Buffer.from(csvContent, 'utf8');

return [
  {
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'text/csv',
        fileName: 'border_crossing_data.csv'
      }
    }
  }
];

