const item = $input.first().json.rss.channel.item;


const rawPubDate = $input.first().json.rss.channel.pubDate;
// Remove day of week (e.g., "Fri, ") using regex or split
const pubDate = rawPubDate.replace(/^\w{3},\s*/, '');

const location = item.title;
const cross_type = item.description.h4.b;

// Extract raw description text
const descText = item.description._;

// Regex helpers
const genMatch = descText.match(/General Lanes:.*?(\d+)\s+min delay\s+(\d+)\s+lane\(s\)/);
const fastMatch = descText.match(/Fast Lanes:.*?(\d+)\s+min delay\s+(\d+)\s+lane\(s\)/);

const genLaneDelay = genMatch ? genMatch[1] : null;
const genLaneNum = genMatch ? genMatch[2] : null;

const fastLaneDelay = fastMatch ? fastMatch[1] : null;
const fastLaneNum = fastMatch ? fastMatch[2] : null;

return [
  {
    json: {
      pubDate,
      location,
      cross_type,
      genLaneDelay,
      genLaneNum,
      fastLaneDelay,
      fastLaneNum
    }
  }
];

